</template>

<script>
import bg1 from './images/bg1_min.png';
import bg2 from './images/bg2_min.png';
import bg3 from './images/bg3_min.png';

export default {
	data() {
		return {
			pictures: {
				loaded: false,
				show: [],
				src: [
					bg1,
					bg2,
					bg3,
				]
			}
		}
	},
	methods: {
		onLoad() {
			if (this.pictures.src.length > 0) {
				this.pictures.show.push(this.pictures.src.pop());
			} else {
				this.pictures.loaded = true;

				const page = this.$refs.page;
				let dir = true;
				const move = () => {
					if (dir) {
						if (page.scrollTop++ === page.scrollTop) {
							dir = false;
						}
					} else {
						if (--page.scrollTop <= 0) {
							dir = true;
						}
					}

				};

				setInterval(move, 30);
			}
		}
	},
	mounted() {
		// 先加载一张图片
		this.pictures.show.push(this.pictures.src.pop());
	}
}
</script>

